{
  "meta": {
    "project": "Meirin Project",
    "version": "1.0.4c",
    "language": "ja",
    "note": "Act1導入の台詞調整と初同調（合体）＝変身のメカニクス可視化。最初の一手『受け→鞭』をSoTへ明記。Act2の1.0.4b（影の王／灯籠光の吸収／胸部貫通）を保持。",
    "narrative": {
      "pov": "first_person",
      "narrator": "メイリン",
      "style": [
        "感情描写を丁寧に",
        "情景描写を丁寧に",
        "会話量を増やす"
      ]
    }
  },
  "theme": {
    "id": "oath-again",
    "title": "OATH AGAIN（再起の誓い）",
    "principles": [
      "居場所＝人と人のつながり",
      "破壊ではなく浄化"
    ]
  },
  "symbols": [
    {
      "id": "lantern",
      "label": "灯籠",
      "meaning": "居場所の光"
    },
    {
      "id": "tea_set",
      "label": "茶器（金継ぎ）",
      "meaning": "壊れても直せる希望"
    },
    {
      "id": "ribbon",
      "label": "リボン",
      "meaning": "意志・絆"
    },
    {
      "id": "bell",
      "label": "鈴音",
      "meaning": "存在の証"
    },
    {
      "id": "home",
      "label": "居場所",
      "meaning": "人のつながり"
    }
  ],
  "world": {
    "setting": "中華風の町と茶館跡",
    "enemy": {
      "id": "shadow",
      "label": "影（カゲロウ）",
      "traits": [
        "墨のにじみのように広がる",
        "光に反発する",
        "嘲笑う声で言葉を発する",
        "奪った灯籠の光を自身に吸収する"
      ],
      "classes": [
        "minor",
        "major",
        "shadow_king"
      ]
    },
    "ally_beasts": [
      {
        "id": "lin",
        "name": "リン",
        "motif": "小さな霊獣（兎＋角）",
        "traits": [
          "淡い光",
          "鈴音"
        ],
        "visibility_rule": "適性者にのみ視認可",
        "function": "支援・同調"
      },
      {
        "id": "fuo",
        "name": "フオニアン",
        "motif": "小さな鳳凰",
        "traits": [
          "炎の粒子",
          "再生の象徴"
        ],
        "visibility_rule": "適性者にのみ視認可",
        "function": "導き"
      }
    ],
    "laws": {
      "resolution": "purification",
      "home_definition": "居場所とは建物ではなく、人の手と心のつながり"
    }
  },
  "forms": [
    {
      "id": "form1",
      "name": "第一形態"
    },
    {
      "id": "form2",
      "name": "第二形態の覚醒解放"
    },
    {
      "id": "fusion",
      "name": "光麟一体"
    }
  ],
  "forms_details": {
    "form1": {
      "aka": "初同調（初変身）",
      "onboarding": [
        "呼吸を合わせる",
        "鈴音を中心に置く",
        "光粒子が衣装／リボンを形成"
      ],
      "capabilities": [
        "受け（盾円）",
        "鞭（打撃）",
        "絡め落とす（拘束→転倒）"
      ],
      "first_move_template": "受け→鞭"
    }
  },
  "stages": [
    {
      "id": "market",
      "label": "市場",
      "symbol": "布とリボン"
    },
    {
      "id": "rooftop",
      "label": "屋上（茶館跡地）",
      "symbol": "灯籠"
    },
    {
      "id": "river",
      "label": "川辺",
      "symbol": "光粒子"
    },
    {
      "id": "ruin_plaza",
      "label": "廃墟の広場",
      "symbol": "暗闇"
    }
  ],
  "character_meirin": {
    "name": "メイリン",
    "age": 14,
    "traits": [
      "おとなしいが芯が強い",
      "居場所への渇望"
    ],
    "speech": [
      "一人称＝わたし",
      "弱さを認めつつ前進"
    ],
    "role": "主人公"
  },
  "supporting_characters": [
    {
      "id": "shuen",
      "name": "シュエン",
      "age": 15,
      "role": "陶器修復師の娘",
      "traits": [
        "落ち着き",
        "芯の強さ"
      ],
      "skills": [
        "金継ぎ（修復）"
      ],
      "visual_notes": [
        "作業着",
        "金粉の跡"
      ],
      "partner_beast_id": "fuo",
      "visibility_rule": "適性者にのみ視認可",
      "function_in_story": "希望の媒介（修復）"
    }
  ],
  "battle": {
    "weapons": [
      "布",
      "リボン",
      "光"
    ],
    "tempo": [
      "受け",
      "躱し",
      "拘束",
      "浄化"
    ],
    "finisher": {
      "id": "fusion_fin",
      "name": "光麟一体"
    },
    "fail_visual": [
      "リボン解ける",
      "鈴音消える",
      "暗転"
    ],
    "win_visual": [
      "灯籠再点灯",
      "浄化の光"
    ],
    "mechanics": {
      "sync_transform": {
        "name": "同調（合体）",
        "trigger": [
          "呼吸同期",
          "鈴音のセンタリング（心の中央に置くイメージ）"
        ],
        "effects": [
          "感覚拡張（音の可視化）",
          "衣装：白＋金／髪の青白い縁取り",
          "リボンの機能拡張（盾／鞭）",
          "光の浄化"
        ]
      }
    },
    "combos": [
      "受け→鞭",
      "受け→鞭→絡め落とす"
    ]
  },
  "visual": {
    "palette": {
      "hero": [
        "白",
        "金",
        "青白い発光"
      ],
      "enemy": [
        "墨色",
        "赤黒い瞳"
      ],
      "symbols": {
        "lantern": "橙",
        "tea_set": "白＋金継ぎ",
        "ribbon": "白金"
      }
    },
    "directives": [
      "逆光",
      "光粒子",
      "音の可視化"
    ]
  },
  "plot": {
    "logline": "壊れた居場所を巡る少女の再起。破壊ではなく浄化で世界を救う。",
    "pages": {
      "act1": [
        1,
        25
      ],
      "act2": [
        26,
        65
      ],
      "act3": [
        66,
        90
      ]
    },
    "beats": {
      "act1": {
        "intro": "茶館の日常（不自然な独白なし）。",
        "inciting": "影の侵入と茶器粉砕。",
        "turn1": "初同調（合体）で第一形態。リンが『盾＝受け』『鞭＝打撃』『光＝浄化』を説明し、最初の一手『受け→鞭』で至近の影をほどく。"
      },
      "act2": {
        "trials": "孤独な戦いと修復の並行、居場所理解の深化。",
        "midpoint": "灯籠の再点灯、人々の協力。",
        "reversal": "廃墟の広場に『影の王』が顕現。灯籠の光は敵へ吸い込まれていく。メイリンは第一形態のまま防戦（リボンで受け・鞭で反撃・拘束と解放の反復）し、膝を震わせ息も絶え絶えに苦戦。背後で『メイリン負けないで！』とシュエンが茶器を修復、茶器から金色の粒子があふれメイリンへ流入。これを契機に第二形態の覚醒解放。しかし影の王は『はははは、この時を待っていたぞ……』と嘲笑し、漆黒に沈む広場から一本の鋭利な触手を放ち、第二形態のメイリンの胸を強烈に貫通。胸の光は触手へ吸収され、リボンは解け、鈴音は消える。閃光後に暗転、変身解除・落下・リン消失へと至る。"
      },
      "act3": {
        "re_oath": "母の言葉と皆の想いで誓い直し（OATH AGAIN）。",
        "climax": "光麟一体で浄化を選び、暴力ではなく『導き』で黒を還す。",
        "finale": "再建された居場所を皆で守る宣言。リン帰還。"
      }
    },
    "storyboards": {
      "act1": {
        "transformation_sequence": [
          {
            "type": "breath",
            "description": "灯籠の残光と湯気の記憶を胸に集め、呼吸同期"
          },
          {
            "type": "chime",
            "description": "鈴音を心の中心に置くイメージ"
          },
          {
            "type": "manifest",
            "description": "髪に青白い縁取り、白＋金の衣装、リボンが浮遊"
          },
          {
            "type": "explain",
            "description": "リンが盾／鞭／浄化を簡潔に説明"
          },
          {
            "type": "first_move",
            "description": "『受け→鞭』で至近の影をほどく"
          }
        ]
      },
      "act2": {
        "opening_panel": {
          "type": "wide",
          "description": "瓦礫の片付け、シュエンと出会い"
        },
        "reversal_sequence": [
          {
            "type": "stage",
            "description": "廃墟の広場。影の王が人型輪郭と複数触手で顕現。灯籠の光が敵に吸い込まれる。"
          },
          {
            "type": "struggle",
            "description": "第一形態のメイリンが防戦。盾→受け、鞭→反撃、拘束→解放の反復。疲弊と息切れ。"
          },
          {
            "type": "prayer",
            "description": "シュエンが茶器を修復し『負けないで！』。茶器から金粒子がメイリンへ流入。"
          },
          {
            "type": "awaken",
            "description": "第二形態の覚醒解放。麒麟の幻影、リボンの光量増。短い押し返し。"
          },
          {
            "type": "taunt",
            "description": "影の王『はははは、この時を待っていたぞ……』。全景が漆黒に。"
          },
          {
            "type": "pierce",
            "description": "巨大触手が胸を貫通。胸部の光が奔流となって吸収される。"
          },
          {
            "type": "collapse",
            "description": "リボン解け・鈴音消失・麒麟像崩壊。光が四散。"
          },
          {
            "type": "fall",
            "description": "変身解除→落下→リン消失→暗転。"
          }
        ]
      }
    }
  }
}

